import React,{useState} from "react"; 
import "./LoginPage.css";
import { useNavigate,Link } from "react-router-dom";
import axios from "axios";


export default function LoginPage() {

    const navigate = useNavigate();
    const [form,setForm] = useState({
        username: '',
        password: ''
    });
    const [error,setError] = useState('');


    const handleChange = (e) => {
        setForm({...form, [e.target.name]: e.target.value});
        setError('');
    };
    

    const handleSubmit =  async (e)=>{
        e.preventDefault();
        try{
            const res = await axios.post("http://localhost:8080/api/auth/login", form);
            const token = res.data.token;
            localStorage.setItem("token", token);
            navigate("/todoshome");
        }catch(error){
            if (error.response && error.response.status === 401) {
                setError("Invalid username or password");
            } else {
                setError("An error occurred. Please try again.");
            }
        }
    }

    return (
        <div className="login-page-wrapper">
            <h1 className="login-title">Login</h1>
            <div className="login-container">
                <form className="login-form" onSubmit={handleSubmit}>
                    <div className="form-group">
                        <label htmlFor="username">Username:</label>
                        <input type="text" id="username" name="username" required value={form.username} onChange={handleChange} />
                    </div>

                    <div className="form-group">
                        <label htmlFor="password">Password:</label>
                        <input type="password" id="password" name="password" required minLength="9" pattern="(?=.*[a-z])(?=.*[A-Z])(?=.*\d).{9,}" value={form.password} onChange={handleChange} />
                    </div>

                    <button type="submit">Login</button>
                    {error && <div style={{ color: 'red', marginBottom: '10px' }}>{error}</div>}
                     <p>
                        <Link to="/register">Don't have an account? Register</Link>
                    </p>
                </form>
                
            </div>
        </div>
    );
}
