import { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";

export default function TodoList() {
  const [todos, setTodos] = useState([]);
  const navigate = useNavigate();

  const fetchTodos = () => {
    fetch("/api/todos", { credentials: "include" })
      .then(res => res.json())
      .then(data => setTodos(Array.isArray(data) ? data : []));
  };

  useEffect(() => {
    fetchTodos();
  }, []);

  const deleteTodo = (id) => {
    fetch(`/api/todos/${id}`, {
      method: "DELETE",
      credentials: "include"
    }).then(fetchTodos);
  };

  const markComplete = (todo) => {
    fetch(`/api/todos/${todo.id}`, {
      method: "PUT",
      headers: { "Content-Type": "application/json" },
      credentials: "include",
      body: JSON.stringify({ ...todo, completed: true })
    }).then(fetchTodos);
  };

  return (
    <div>
      <h2>üìã All Todos</h2>
      {todos.length === 0 ? (
        <p>No todos found.</p>
      ) : (
        <ul className="todo-list">
          {todos.map(todo => (
            <li key={todo.id} className={`todo-item ${todo.completed ? "completed" : ""}`}>
              <div>
                <strong>{todo.description}</strong> <br />
                <small>Due: {new Date(todo.targetDate).toLocaleDateString()}</small>
              </div>
              <div className="todo-actions">
                <button onClick={() => navigate(`/todoshome/edit/${todo.id}`)}>‚úèÔ∏è</button>
                <button onClick={() => deleteTodo(todo.id)}>üóëÔ∏è</button>
                {!todo.completed && (
                  <button onClick={() => markComplete(todo)}>‚úÖ</button>
                )}
              </div>
            </li>
          ))}
        </ul>
      )}
    </div>
  );
}
