// src/components/TodoForm.jsx
import { useState, useEffect } from "react";
import { Link, useNavigate, useParams } from "react-router-dom";
import axios from "axios";
import "./TodoHomeLayout.css";
import AxiosInstance from "./AxiosIntance";
export default function TodoForm() {
  const { id } = useParams(); // used for edit mode
  const navigate = useNavigate();

  const [description, setDescription] = useState("");
  const [targetDate, setTargetDate] = useState("");
  const [error, setError] = useState("");

  const isEdit = Boolean(id);

  useEffect(() => {
    if (isEdit) {
      axios
        .get(`/api/todos/${id}`)
        .then((res) => {
          setDescription(res.data.description);
          setTargetDate(res.data.targetDate.slice(0, 16)); // for datetime-local input
        })
        .catch(() => setError("Todo not found or failed to fetch."));
    }
  }, [id, isEdit]);

  const handleSubmit = (e) => {
    e.preventDefault();

    const todoData = {
      description,
      targetDate: targetDate.replace("Z", ""), // Remove Z if present
    };

    const request = isEdit
      ? AxiosInstance.put(`/todos/${id}`, todoData)
      : AxiosInstance.post("/todos", todoData);

    request
      .then(() => navigate("/todoshome/todos"))
      .catch((err) => {
        const msg = err.response?.data?.error || "Something went wrong.";
        setError(msg);
      });
  };

  return (
    <div className="todo-form-container">
      <h2>{isEdit ? "Edit Todo" : "Create New Todo"}</h2>

      {error && <div className="error-msg">{error}</div>}

      <form onSubmit={handleSubmit} className="todo-form">
        <label>Description:</label>
        <input
          type="text"
          value={description}
          onChange={(e) => setDescription(e.target.value)}
          required
        />

        <label>Target Date:</label>
        <input
          type="datetime-local"
          value={targetDate}
          onChange={(e) => setTargetDate(e.target.value)}
          required
        />

        <button type="submit">{isEdit ? "Update" : "Create"}</button>
        <Link to="/todoshome" >Go to Home</Link>
      </form>
    </div>
  );
}
