// src/components/AllTodos.jsx
import { useEffect, useState } from "react";
import axios from "axios";
import "./AllTodos.css";

export default function AllTodos() {
  const [todos, setTodos] = useState([]);
  const [error, setError] = useState("");

  useEffect(() => {
    axios
      .get("/api/todos", {
        headers: {
          userId: 1, // Replace with dynamic auth value
        },
      })
      .then((res) => setTodos(res.data))
      .catch((err) => {
        console.error(err);
        setError("Failed to load todos.");
      });
  }, []);

  const markCompleted = (id) => {
    axios
      .put(`/api/todos/${id}`, { completed: true }, { headers: { userId: 1 } })
      .then((res) => {
        setTodos((prev) => prev.map((todo) => (todo.id === id ? res.data : todo)));
      })
      .catch((err) => alert("Error marking as completed"));
  };

  const deleteTodo = (id) => {
    axios
      .delete(`/api/todos/${id}`, { headers: { userId: 1 } })
      .then(() => setTodos((prev) => prev.filter((todo) => todo.id !== id)))
      .catch((err) => alert("Error deleting todo"));
  };

  return (
    <div className="all-todos">
      <h2>üìã All Todos</h2>
      {error && <p className="error">{error}</p>}
      <ul className="todo-list">
        {todos.map((todo) => (
          <li key={todo.id} className={`todo-item ${todo.completed ? "completed" : ""}`}>
            <span>{todo.description}</span>
            <span className="date">üóì {todo.targetDate?.slice(0, 10)}</span>
            <div className="actions">
              <button onClick={() => markCompleted(todo.id)} title="Mark as Completed">‚úÖ</button>
              <button onClick={() => alert("Edit not implemented yet")}>‚úèÔ∏è</button>
              <button onClick={() => deleteTodo(todo.id)} title="Delete">üóëÔ∏è</button>
            </div>
          </li>
        ))}
      </ul>
    </div>
  );
}
