import "./TodoHomePage.css";
import { Link, Outlet, useLocation } from "react-router-dom";
import { useState } from "react";

export default function TodoHomePage() {
  const [search, setSearch] = useState("");
  const location = useLocation();

  return (
    <div className="todo-layout">
      <header className="todo-header">
        <div className="todo-title">My Todo App</div>
        <div className="header-right">
          <span className="profile-name">John Doe</span>
          <Link to="/logout" className="logout-link" title="Logout">
            ⏻
          </Link>
        </div>
      </header>
      <div className="container">
        <div className="sidebar-area">
          <form
            className="todo-search-vertical"
            onSubmit={(e) => {
              e.preventDefault();
              alert(`Searching for: ${search}`);
            }}
          >
            <input
              type="text"
              placeholder="Search tasks..."
              value={search}
              onChange={(e) => setSearch(e.target.value)}
            />
            <button type="submit" title="Search">
              Search
            </button>
          </form>
          <nav className="sidebar">
            <ul>
              <li>
                <Link to="todos">All Todos</Link>
              </li>
              <li>
                <Link to="create">➕ Create Todo</Link>
              </li>
              <li>
                <Link to="completed">✅ Completed Todos</Link>
              </li>
              <li>
                <Link to="pending">⏳ Pending Todos</Link>
              </li>
              <li>
                <Link to="/logout">⏻ Logout</Link> {/* Keep logout absolute if it's not nested */}
              </li>
            </ul>
          </nav>
        </div>
        <main className="main">
          {location.pathname === "/todoshome" ? (
            <div className="getting-started">
              <h2>Welcome to My Todo App!</h2>
              <p>Select an option from the sidebar to get started.</p>
            </div>
          ) : (
            <Outlet />
          )}
        </main>
      </div>
    </div>
  );
}
