// src/components/SearchResults.jsx
import { useEffect, useState } from "react";
import { useSearchParams } from "react-router-dom";
import axios from "axios";
import "./TodoHomeLayout.css";

export default function SearchResults() {
  const [searchParams] = useSearchParams();
  const [results, setResults] = useState([]);
  const [error, setError] = useState("");
  const query = searchParams.get("description");

  useEffect(() => {
    if (query) {
      axios
        .get(`/api/todos?description=${encodeURIComponent(query)}`)
        .then((res) => {
          setResults(res.data);
          setError("");
        })
        .catch((err) => {
          setError("No matching todos found.");
          setResults([]);
        });
    }
  }, [query]);

  return (
    <div className="search-results-container">
      <h2>Search Results for "{query}"</h2>
      {error && <p className="error-msg">{error}</p>}
      <div className="results-list">
        {results.map((todo) => (
          <div
            className={`search-card ${todo.completed ? "completed" : "pending"}`}
            key={todo.id}
          >
            <h3>{todo.description}</h3>
            <p>
              Target Date: {new Date(todo.targetDate).toLocaleString()} <br />
              Status:{" "}
              <strong style={{ color: todo.completed ? "green" : "#f39c12" }}>
                {todo.completed ? "Completed" : "Pending"}
              </strong>
            </p>
          </div>
        ))}
      </div>
    </div>
  );
}
