import { useState } from "react";
import "./AllTodos.css";

export default function AllTodos() {
  // Mock data for frontend-only testing
  const [todos, setTodos] = useState([
    {
      id: 1,
      description: "Buy groceries",
      targetDate: "2025-07-24",
      completed: false,
    },
    {
      id: 2,
      description: "Finish project",
      targetDate: "2025-07-25",
      completed: true,
    },
    {
      id: 3,
      description: "Read a book",
      targetDate: "2025-07-26",
      completed: false,
    },
  ]);
  const [error] = useState("");

  const markCompleted = (id) => {
    setTodos((prev) =>
      prev.map((todo) =>
        todo.id === id ? { ...todo, completed: true } : todo
      )
    );
  };

  const deleteTodo = (id) => {
    setTodos((prev) => prev.filter((todo) => todo.id !== id));
  };

  return (
    <div className="all-todos">
      <h2>üìã All Todos</h2>
      {error && <p className="error">{error}</p>}
      <ul className="todo-list">
        {todos.map((todo) => (
          <li key={todo.id} className={`todo-item ${todo.completed ? "completed" : ""}`}>
            <span>{todo.description}</span>
            <span className="date">üóì {todo.targetDate}</span>
            <div className="actions">
              <button onClick={() => markCompleted(todo.id)} title="Mark as Completed">‚úÖ</button>
              <button onClick={() => alert("Edit not implemented yet")}>‚úèÔ∏è</button>
              <button onClick={() => deleteTodo(todo.id)} title="Delete">üóëÔ∏è</button>
            </div>
          </li>
        ))}
      </ul>
    </div>
  );
}
