import { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import {
  Table, TableBody, TableCell, TableContainer, TableHead, TableRow, Paper, Button, Typography
} from "@mui/material";

export default function TodoList() {
  const [todos, setTodos] = useState([]);
  const navigate = useNavigate();

  // Fetch todos from backend
  const fetchTodos = () => {
    fetch("/api/todos", { credentials: "include" })
      .then(res => res.json())
      .then(data => setTodos(Array.isArray(data) ? data : []));
  };

  useEffect(() => {
    fetchTodos();
  }, []);

  // Delete todo by id
  const deleteTodo = (id) => {
    fetch(`/api/todos/${id}`, {
      method: "DELETE",
      credentials: "include"
    }).then(fetchTodos);
  };

  // Mark todo as completed
  const markComplete = (todo) => {
    fetch(`/api/todos/${todo.id}`, {
      method: "PUT",
      headers: { "Content-Type": "application/json" },
      credentials: "include",
      body: JSON.stringify({ ...todo, completed: true })
    }).then(fetchTodos);
  };

  return (
    <div>
      <Typography variant="h5" gutterBottom>📋 All Todos</Typography>
      {todos.length === 0 ? (
        <Typography>No todos found.</Typography>
      ) : (
        <TableContainer component={Paper} sx={{ maxWidth: 700, margin: "auto" }}>
          <Table>
            <TableHead>
              <TableRow>
                <TableCell>Description</TableCell>
                <TableCell>Due Date</TableCell>
                <TableCell>Status</TableCell>
                <TableCell>Actions</TableCell>
              </TableRow>
            </TableHead>
            <TableBody>
              {todos.map(todo => (
                <TableRow key={todo.id} sx={{ backgroundColor: todo.completed ? "#e6ffe6" : "#fffbe6" }}>
                  <TableCell>{todo.description}</TableCell>
                  <TableCell>{new Date(todo.targetDate).toLocaleDateString()}</TableCell>
                  <TableCell>
                    <span style={{ color: todo.completed ? "green" : "#f39c12", fontWeight: "bold" }}>
                      {todo.completed ? "Completed" : "Pending"}
                    </span>
                  </TableCell>
                  <TableCell>
                    <Button
                      variant="outlined"
                      color="primary"
                      size="small"
                      onClick={() => navigate(`/todoshome/edit/${todo.id}`)}
                      sx={{ mr: 1 }}
                    >
                      ✏️
                    </Button>
                    <Button
                      variant="outlined"
                      color="error"
                      size="small"
                      onClick={() => deleteTodo(todo.id)}
                      sx={{ mr: 1 }}
                    >
                      🗑️
                    </Button>
                    {!todo.completed && (
                      <Button
                        variant="contained"
                        color="success"
                        size="small"
                        onClick={() => markComplete(todo)}
                      >
                        ✅
                      </Button>
                    )}
                  </TableCell>
                </TableRow>
              ))}
            </TableBody>
          </Table>
        </TableContainer>
      )}
    </div>
  );
}
