import "./TodoHomeLayout.css";
import { Link, Outlet, useLocation, useNavigate } from "react-router-dom";
import { useState } from "react";
import LiveClock from "./LiveClock";

export default function TodoLayout() {
  const [search, setSearch] = useState("");
  const navigate = useNavigate();
  const location = useLocation();

  return (
    <div className="todo-layout">
      <header className="todo-header">
        <div className="todo-title">My Todo App</div>
        <div className="header-profile-vertical">
          <span className="profile-name">Kiran Kumar Reddy</span>
          <Link to="/" className="logout-link" title="Logout">
            ‚èª Logout
          </Link>
  
        </div>
        <LiveClock/>
      </header>

      <div className="container">
        {/* Sidebar */}
        <div className="sidebar-area">
          <form
            className="todo-search-vertical"
            onSubmit={(e) => {
              e.preventDefault();
              navigate(`/todoshome/search?description=${search}`);
            }}
          >
            <input
              type="text"
              placeholder="Search tasks..."
              value={search}
              onChange={(e) => setSearch(e.target.value)}
            />
            <button type="submit" title="Search">
              Search
            </button>
          </form>

          <nav className="sidebar">
            <ul>
              <li>
                <Link to="create">‚ûï Create Todo</Link>
              </li>
              <li>
                <Link to="todos">üìãAll Todos</Link>
              </li>
            </ul>
          </nav>
        </div>

        {/* Main View */}
        <main className="main">
          {location.pathname === "/todoshome" ? (
            <div className="getting-started">
              <h2>Welcome, Kiran Kumar Reddy! üéâ</h2>
              <p>Select an option from the sidebar to get started.</p>
              {/* You can also show summary stats here if you want */}
            </div>
          ) : (
            <Outlet />
          )}
        </main>
      </div>
    </div>
  );
}
