import "./TodoHomePage.css";
import { Link, Outlet, useLocation } from "react-router-dom";
import { useState } from "react";

export default function TodoHomePage() {
  const [search, setSearch] = useState("");
  const location = useLocation();
  const userName = "John Doe"; // Replace with actual user name from your auth logic

  return (
    <div className="todo-layout">
      <header className="todo-header">
        <div className="todo-title">My Todo App</div>
        <form
          className="todo-search"
          onSubmit={e => {
            e.preventDefault();
            alert(`Searching for: ${search}`);
          }}
        >
          <input
            type="text"
            placeholder="Search tasks..."
            value={search}
            onChange={e => setSearch(e.target.value)}
          />
          <button type="submit">Search</button>
        </form>
        <div className="profile-section">
          <div className="profile-avatar">{userName[0]}</div>
          <span className="profile-name">{userName}</span>
        </div>
      </header>
      <div className="container">
        <nav className="sidebar">
          <ul>
            <li><Link to="create">Create Todo</Link></li>
            <li><Link to="todos">All Todos</Link></li>
            <li><Link to="completed">Completed Todos</Link></li>
            <li><Link to="pending">Pending Todos</Link></li>
            <li><Link to="logout">Logout</Link></li>
          </ul>
        </nav>
        <main className="main">
          {location.pathname === "/todoshome" ? (
            <div className="getting-started">
              <h2>Welcome to My Todo App!</h2>
              <p>Select an option from the sidebar to get started.</p>
            </div>
          ) : (
            <Outlet />
          )}
        </main>
      </div>
    </div>
  );
}
