import { useState } from "react";
import { useNavigate } from "react-router-dom";

export default function TodoList() {
  const [todos, setTodos] = useState([
    { id: 1, description: "Buy groceries", targetDate: "2025-07-24", completed: false },
    { id: 2, description: "Finish project", targetDate: "2025-07-25", completed: true },
  ]);
  const navigate = useNavigate();

  const deleteTodo = (id) => {
    setTodos(prev => prev.filter(todo => todo.id !== id));
  };

  const markComplete = (todo) => {
    setTodos(prev =>
      prev.map(t =>
        t.id === todo.id ? { ...t, completed: true } : t
      )
    );
  };

  return (
    <div>
      <h2>üìã All Todos</h2>
      {todos.length === 0 ? (
        <p>No todos found.</p>
      ) : (
        <table className="todo-table">
          <thead>
            <tr>
              <th>Description</th>
              <th>Due Date</th>
              <th>Status</th>
              <th>Actions</th>
            </tr>
          </thead>
          <tbody>
            {todos.map(todo => (
              <tr key={todo.id} className={todo.completed ? "completed" : "pending"}>
                <td>{todo.description}</td>
                <td>{todo.targetDate}</td>
                <td>
                  <span style={{ color: todo.completed ? "green" : "#f39c12", fontWeight: "bold" }}>
                    {todo.completed ? "Completed" : "Pending"}
                  </span>
                </td>
                <td>
                  <button onClick={() => navigate(`/todoshome/edit/${todo.id}`)}>‚úèÔ∏è</button>
                  <button onClick={() => deleteTodo(todo.id)}>üóëÔ∏è</button>
                  {!todo.completed && (
                    <button onClick={() => markComplete(todo)}>‚úÖ</button>
                  )}
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      )}
    </div>
  );
}
