import React,{useState} from 'react'
import "./LoginPage.css";
import { useNavigate, Link } from 'react-router-dom';


export default function RegisterPage() {
  const navigate = useNavigate();
  const [form,setForm] = useState({
    username: '',
    password: ''
  });
  const[error,setError] = useState('');

  const handleChange = (e) => {
    setForm({...form, [e.target.name]: e.target.value});
    setError('');
  };

    const handleSubmit = (event) => {
        event.preventDefault();
        if (!form.username.trim() || !form.password.trim()) {
            setError('Username and Password are required');
            return;
        }
        if(form.password.length<6){
            setError('Password must be at least 6 characters long');
            return;
        }
        navigate("/");
    };

    return (
        <div className="login-page-wrapper">
            <h1 className="login-title">Register</h1>
            <div className="login-container">
                <form className="login-form" onSubmit={handleSubmit}>
                    <div className="form-group">
                        <label htmlFor="username">Username:</label>
                        <input type="text" id="username" name="username" value={form.username} onChange={handleChange} />
                    </div>
                    <div className="form-group">
                        <label htmlFor="password">Password:</label>
                        <input type="password" id="password" name="password" required minLength="6" pattern="(?=.*[a-z])(?=.*[A-Z])(?=.*\d).{8,0}" value={form.password} onChange={handleChange} />
                    </div>
                    {error && <div style={{ color: 'red', marginBottom: '10px' }}>{error}</div>}
                    <button type="submit">Register</button>
                     <p>
                        <Link to="/">Already Have an Account, Login</Link>
                    </p>
                </form>
            </div>
        </div>
    );
}
