// src/components/SearchResults.jsx
import { useEffect, useState } from "react";
import { useSearchParams } from "react-router-dom";
import "./TodoHomeLayout.css";
import AxiosInstance from "./AxiosIntance"; // Import your Axios instance

export default function SearchResults() {
  const [searchParams] = useSearchParams();
  const [results, setResults] = useState([]);
  const [error, setError] = useState("");
  const query = searchParams.get("description");

  useEffect(() => {
  if (query) {
    AxiosInstance
      .get(`/todos?description=${encodeURIComponent(query)}`)
      .then((res) => {
        setResults(res.data);
        console.log(res.data)
        setError("");
      })
      .catch((err) => {
        setError("No matching todos found.");
        setResults([]);
      });
  }
  else {
    // If query is empty, clear results and error
    setResults([]);
    setError("");
  }
}, [query]);

  return (
    <div className="search-results-container">
      <h2>Search Results for "{query}"</h2>
      {error && <p className="error-msg">{error}</p>}
      <div className="results-list">
        {results.length === 0 ? (
          <p className="error-msg">No todos found.</p>
        ) : (
          results.map((todo) => (
            <div
              className={`search-card ${todo.done ? "completed" : "pending"}`}
              key={todo.id}
            >
              <h3>{`Description: ${todo.description}`}</h3>
              <h5>
                Target Date: {new Date(todo.targetDate).toLocaleString()} <br />
                Status:{" "}
                <strong style={{ color: todo.done ? "green" : "#f39c12" }}>
                  {todo.done ? "Completed" : "Pending"}
                </strong>
              </h5>
            </div>
          ))
        )}
      </div>
    </div>
  );
}
