import { useState } from "react";
import { useNavigate } from "react-router-dom";
import {
  Table, TableBody, TableCell, TableContainer, TableHead, TableRow, Paper, Button, Typography
} from "@mui/material";

export default function TodoList() {
  // Mock data for demonstration
  const [todos, setTodos] = useState([
    { id: 1, description: "Buy groceries", targetDate: "2025-07-24", completed: false },
    { id: 2, description: "Finish project", targetDate: "2025-07-25", completed: true },
    { id: 3, description: "Read a book", targetDate: "2025-07-26", completed: false },
  ]);
  const navigate = useNavigate();

  const deleteTodo = (id) => {
    setTodos(prev => prev.filter(todo => todo.id !== id));
  };

  const markComplete = (todo) => {
    setTodos(prev =>
      prev.map(t =>
        t.id === todo.id ? { ...t, completed: true } : t
      )
    );
  };

  return (
    <div>
      <Typography variant="h5" gutterBottom>üìã All Todos</Typography>
      {todos.length === 0 ? (
        <Typography>No todos found.</Typography>
      ) : (
        <TableContainer component={Paper} sx={{ maxWidth: 700, margin: "auto" }}>
          <Table>
            <TableHead>
              <TableRow>
                <TableCell>Description</TableCell>
                <TableCell>Due Date</TableCell>
                <TableCell>Status</TableCell>
                <TableCell>Actions</TableCell>
              </TableRow>
            </TableHead>
            <TableBody>
              {todos.map(todo => (
                <TableRow key={todo.id} sx={{ backgroundColor: todo.completed ? "#e6ffe6" : "#fffbe6" }}>
                  <TableCell>{todo.description}</TableCell>
                  <TableCell>{todo.targetDate}</TableCell>
                  <TableCell>
                    <span style={{ color: todo.completed ? "green" : "#f39c12", fontWeight: "bold" }}>
                      {todo.completed ? "Completed" : "Pending"}
                    </span>
                  </TableCell>
                  <TableCell>
                    <Button
                      variant="outlined"
                      color="primary"
                      size="small"
                      onClick={() => navigate(`/todoshome/edit/${todo.id}`)}
                      sx={{ mr: 1 }}
                    >
                      ‚úèÔ∏è
                    </Button>
                    <Button
                      variant="outlined"
                      color="error"
                      size="small"
                      onClick={() => deleteTodo(todo.id)}
                      sx={{ mr: 1 }}
                    >
                      üóëÔ∏è
                    </Button>
                    {!todo.completed && (
                      <Button
                        variant="contained"
                        color="success"
                        size="small"
                        onClick={() => markComplete(todo)}
                      >
                        ‚úÖ
                      </Button>
                    )}
                  </TableCell>
                </TableRow>
              ))}
            </TableBody>
          </Table>
        </TableContainer>
      )}
    </div>
  );
}
