import { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";

export default function TodoList() {
  const [todos, setTodos] = useState([]);
  const navigate = useNavigate();


  const fetchTodos = () => {
    fetch("/api/todos", { credentials: "include" })
      .then(res => res.json())
      .then(data => setTodos(Array.isArray(data) ? data : []));
  };

  useEffect(() => {
    fetchTodos();
  }, []);

  const deleteTodo = (id) => {
    fetch(`/api/todos/${id}`, {
      method: "DELETE",
      credentials: "include"
    }).then(fetchTodos);
  };

  const markComplete = (todo) => {
    fetch(`/api/todos/${todo.id}`, {
      method: "PUT",
      headers: { "Content-Type": "application/json" },
      credentials: "include",
      body: JSON.stringify({ ...todo, completed: true })
    }).then(fetchTodos);
  };

  return (
    <div>
      <h2>üìã All Todos</h2>
      {todos.length === 0 ? (
        <p>No todos found.</p>
      ) : (
        <table className="todo-table">
          <thead>
            <tr>
              <th>Description</th>
              <th>Due Date</th>
              <th>Status</th>
              <th>Actions</th>
            </tr>
          </thead>
          <tbody>
            {todos.map(todo => (
              <tr key={todo.id} className={todo.completed ? "completed" : "pending"}>
                <td>{todo.description}</td>
                <td>{new Date(todo.targetDate).toLocaleDateString()}</td>
                <td>
                  <span style={{ color: todo.completed ? "green" : "#f39c12", fontWeight: "bold" }}>
                    {todo.completed ? "Completed" : "Pending"}
                  </span>
                </td>
                <td>
                  <button onClick={() => navigate(`/todoshome/edit/${todo.id}`)}>‚úèÔ∏è</button>
                  <button onClick={() => deleteTodo(todo.id)}>üóëÔ∏è</button>
                  {!todo.completed && (
                    <button onClick={() => markComplete(todo)}>‚úÖ</button>
                  )}
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      )}
    </div>
  );
}
