import { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";

export default function TodoList() {
  // const [todos, setTodos] = useState([]);
  const navigate = useNavigate();

  // Mock data for frontend-only testing
  // Comment out fetchTodos and useEffect if you want to use this mock data
  // useEffect(() => {
  //   setTodos([
  //     { id: 1, description: "Buy groceries", targetDate: new Date().toISOString(), completed: false },
  //     { id: 2, description: "Finish project", targetDate: new Date().toISOString(), completed: true },
  //   ]);
  // }, []);

  // const fetchTodos = () => {
  //   fetch("/api/todos", { credentials: "include" })
  //     .then(res => res.json())
  //     .then(data => setTodos(Array.isArray(data) ? data : []));
  // };

  // useEffect(() => {
  //   fetchTodos();
  // }, []);

  const deleteTodo = (id) => {
    fetch(`/api/todos/${id}`, {
      method: "DELETE",
      credentials: "include"
    }).then(fetchTodos);
  };

  // const markComplete = (todo) => {
  //   fetch(`/api/todos/${todo.id}`, {
  //     method: "PUT",
  //     headers: { "Content-Type": "application/json" },
  //     credentials: "include",
  //     body: JSON.stringify({ ...todo, completed: true })
  //   }).then(fetchTodos);
  // };
const [todos, setTodos] = useState([
    {
      id: 1,
      description: "Buy groceries",
      targetDate: "2025-07-24",
      completed: false,
    },
    {
      id: 2,
      description: "Finish project",
      targetDate: "2025-07-25",
      completed: true,
    },
    {
      id: 3,
      description: "Read a book",
      targetDate: "2025-07-26",
      completed: false,
    },
  ]);
  return (
    <div>
      <h2>📋 All Todos</h2>
      {todos.length === 0 ? (
        <p>No todos found.</p>
      ) : (
        <table className="todo-table">
          <thead>
            <tr>
              <th>Description</th>
              <th>Due Date</th>
              <th>Status</th>
              <th>Actions</th>
            </tr>
          </thead>
          <tbody>
            {todos.map(todo => (
              <tr key={todo.id} className={todo.completed ? "completed" : "pending"}>
                <td>{todo.description}</td>
                <td>{new Date(todo.targetDate).toLocaleDateString()}</td>
                <td>
                  <span style={{ color: todo.completed ? "green" : "#f39c12", fontWeight: "bold" }}>
                    {todo.completed ? "Completed" : "Pending"}
                  </span>
                </td>
                <td>
                  <button onClick={() => navigate(`/todoshome/edit/${todo.id}`)}>✏️</button>
                  <button onClick={() => deleteTodo(todo.id)}>🗑️</button>
                  {!todo.completed && (
                    <button onClick={() => markComplete(todo)}>✅</button>
                  )}
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      )}
    </div>
  );
}
